//
// Additional formats for ChartNew.js
//
// see https://github.com/FVANCOP/ChartNew.js/wiki/900_010_format.js
//
function fmtChartJSPerso(e,t,n){var r=["Sun","Mon","Tue","Wed","Thu","Fri","Sat"];switch(n.split(/[\s,]+/)[0].toUpperCase()){case"DATE":spltdt=n.replace(/,/g," ").replace(/:/g," ").split(/[\s,]+/);var i=new Array;for(var s=1;s<spltdt.length;s++){switch(spltdt[s].toUpperCase()){case"WEEKDAY":case"YEAR":case"MONTH":case"DAY":i[spltdt[s]]=spltdt[s+1];break;default:break}}return_value=t.toLocaleDateString(n.split(" ")[1],i);break;case"FMTDATE":spltdt=n.split(/[\s,]+/)[1].toUpperCase();return_value=spltdt.replaceArray(["DD","MM","YYYY","YY"],[t.getDate(),1+t.getMonth(),t.getFullYear(),t.getYear()%100]);break;case"TIME":return_value=t.toLocaleTimeString();break;case"FMTTIME":spltdt=n.split(/[\s,]+/)[1].toUpperCase();return_value=spltdt.replaceArray(["HH","MM","SS"],[t.getHours()<10?"0"+t.getHours():t.getHours(),t.getMinutes()<10?"0"+t.getMinutes():t.getMinutes(),t.getSeconds()<10?"0"+t.getSeconds():t.getSeconds()]);break;case"FMTDATETIME":spltdt=n.splitLimit(/[\s,]+/,2)[1];return_value=spltdt.replaceArray(["DD","MM","YYYY","YY","HH","mm","ss","WD"],[t.getDate(),1+t.getMonth(),t.getFullYear(),t.getYear()%100,t.getHours()<10?"0"+t.getHours():t.getHours(),t.getMinutes()<10?"0"+t.getMinutes():t.getMinutes(),t.getSeconds()<10?"0"+t.getSeconds():t.getSeconds(),r[t.getDay()]]);break;default:return_value=t;break}return return_value}String.prototype.replaceArray=function(e,t){var n=this;var r;for(var i=0;i<e.length;i++){r=new RegExp(e[i],"g");n=n.replace(r,t[i])}return n};String.prototype.splitLimit=function(e,t){var n=this;var r=[];var i=n.search(e);if(i<0)return false;r.push(n.substring(0,i));r.push(n.substring(i+1));return r}
